mapper.update:
UPDATE TB_BOARD
	SET  TITLE=#{title , jdbcType=VARCHAR},
		 CONTENTS=#{contents, jdbcType=VARCHAR}				
	WHERE IDX=#{idx}

UPDATE TB_BOARD A
	SET  COUNT=IFNULL(A.COUNT,0)+1
	WHERE A.IDX=#{idx}

mapper.select:
SELECT NAME
FROM TB_USR
WHERE 1=1
AND ID=#{userId}
AND PASSWORD=#{password}

mapper.delete:
 				DEELET FROM TB_ATTACH	
				WHERE 1=1 
				AND A.IDX=#{idx}
